import UIKit
import RealmSwift

enum CategoryListModel {
    // MARK: Fetch Categories
    
    enum FetchCategories {
        struct Response {
            var categories: Results<Category>?
        }
        struct ViewModel {
            var categories: Array<Category>?
            var errorString: String?
        }
    }
    
    
    // MARK: Update Categories
    
    enum UpdateCategories {
        struct Request {
            enum Method {
                case add
                case remove
            }
            var method: Method
            var category: Category
        }
        struct Response {
            var addedCategory: Category?
            var removedCategory: Category?
            var categories: Array<Category>? = []
            var error: Error?
        }
        struct ViewModel {
            var categories: Array<Category>? = []
            var errorString: String?
        }
    }
    
    // MARK: Filter Categories
    
    enum FilterCategories {
        struct Request {
            var text: String?
        }
        struct Response {
            var categories: Results<Category>?
            var error: Error?
        }
        struct ViewModel {
            var categories: Array<Category>? = []
            var errorString: String?
        }
    }
}
